#define HW_UART0_S2_CLR(x, v)    (BME_AND8(HW_UART0_S2_ADDR(x), (uint8_t)(~(v))))
#define HW_UART0_S2_TOG(x, v)    (BME_XOR8(HW_UART0_S2_ADDR(x), (uint8_t)(v)))
/*@}*/

